function getWordTagRandomKeyValue(tbl)
  local count = 0
  -- 使用pairs遍历表中的所有键值对
  for key, value in pairs(tbl) do
      count = count + 1
      -- 如果是随机选取第一个键值对，则直接返回
      if count == 1 then
          ch = key
          enAndEn2 = value
      else
          -- 如果不是第一个键值对，有一定的概率选择新的键值对
          local probability = math.random(1, count)
          if probability == 1 then
              ch = key
              enAndEn2 = value
          end
      end
  end
  -- n.胡萝卜        carrot,carrot
  -- print(ch, enAndEn2)
